mapper.select:
SELECT id_ FROM bq_topics


		AND enable_ = #{cm.enable}


		AND audit_ = #{cm.audit}


		and is_hot = 1


		and is_hot = 0


		and owner_id = #{cm.ownerId}


		and name_ = #{cm.name}


		and summary_ = #{cm.summary}


		and (name_ like CONCAT('%',#{cm.keyword},'%') or summary_ like CONCAT('%',#{cm.keyword},'%'))


ORDER BY


		view_num DESC


		view_num DESC


		is_hot DESC,hot_index ASC
